let textToCharList text =
    [for t in text -> t]

let rec whatFloor (text : char list) =
    List.fold (fun acc x -> if x = '(' then acc + 1 else acc-1) 0 text
    

[<EntryPoint>]
let main argv = 
    let answer = whatFloor( textToCharList text )
    printfn "%d" answer
    System.Console.ReadLine() |> ignore
